    def "can correctly count number of islands after each addLand operation"(int m, int n, int[][] positions, List<Integer> islandCounts) {
        expect:
            islandCounter2.count(m, n, positions) == islandCounts

        where:
            m | n | positions                                                || islandCounts
            3 | 0 | [[0, 0]]                                                 || []
            0 | 3 | [[0, 0]]                                                 || []
            3 | 3 | []                                                       || []
            3 | 3 | [[0, 0], [0, 1], [1, 2], [2, 1]]                         || [1, 1, 2, 3]
